<template>
    <view class="error">
		<view class="error-icon"></view>
		<text class="error-text">请求出错</text>
		<text class="error-text" v-if="errorMsg.length > 0">{{errorMsg}}</text>
		<text class="btn" type="default" @click="retry">重试</text>
    </view>
</template>

<script>
    export default {
		props: {
			errorMsg: {
				type: String,
				default: ''
			},
		},
		methods: {
			retry() {
				this.$emit("tryAgain")
			},
		}
	
    }
</script>

